mapper.insert:
insert into files (id, file_name, file_path, file_size, 
    type, parent_table, parent_no)
    values (
        #{id}, #{fileName}, #{filePath}, #{fileSize},
        #{type}, #{parentTable}, #{parentNo}
    )

mapper.update:
update files
    set file_name = #{fileName},
        file_path = #{filePath},
        file_size = #{fileSize},
        type = #{type},
        parent_table = #{parentTable}
        parent_no =#{parentNo}
where id= #{id}

mapper.select:
select * 
from files
where parent_table = #{parentTable}
  and parent_no = #{parentNo}

mapper.delete:
delete 
from files
where parent_table = #{parentTable}
  and parent_no = #{parentNo}
